-- Fix Formation for Caliph Scorpidsting 7847 & Wastewander Rogue Adds
-- general cleanup
UPDATE `creature` SET `modelid` = 0, `spawndist` = 0 WHERE `guid` IN (23286,23467,23466);
UPDATE `creature` SET `MovementType` = 0 WHERE `guid` IN (23467,23466);
-- Group Linking, known issue is that leading npc is not temporarily substituted due to loosecontrol spells like sap/distract
-- linked guids do not enter evade mode, therefore stealth is not recasted on group evade
DELETE FROM `creature_linking` WHERE `master_guid` = 23286;
-- Port his movement to creature_movement_template
DELETE FROM `creature_movement` WHERE `id` IN (23286,23467,23466);
DELETE FROM `creature_movement_template` WHERE `entry` = 7847;
